import React, { useState} from 'react';
import { Link } from 'react-router-dom';
import { FiArrowLeft} from 'react-icons/fi';//eu acho o FiArrowLeft procurando em feather icons
import './styles.css';
import api from '../../services/api';
import logoImg from '../../assets/logo.svg';

export default  function Register(){
    const[name, setName] = useState('');
    const[email, setEmail] = useState('');
    const[whatsapp, setWhatsapp] = useState('');
    const[city, setCity] = useState('');
    const[uf, setUf] = useState('');
    
    function handleRegister(e){//responsavel por fazer o cadastro do usuario
        e.preventDefault();
    }
    return (
        <div className="register-container">
            <div className="content">
                <section>
                    <img src={logoImg} alt="Be the hero"/>
                    <h1>Cadastro</h1>
                    <p>Faça seu cadastro, entre na plataforma e ajude pessoas a encontrarem os casos da sua ONG.</p>

                    <Link className="back-link" to="/">
                        <FiArrowLeft size={16} color="#E02041" />
                    Não tenho cadastro</Link>
                </section>

                <form onSubmit={handleRegister}>
                    <input
                     placeholder="Nome da ONG"
                     value={name}
                     onChange={e => setName(e.target.value)}//e.target.value é o valor do input e to colocando ele dentro da variável que a gente ta armazenando no estado
                    />
                    <input 
                    type="email" 
                    placeholder="E-mail" 
                    value={email}
                    onChange={e => setEmail(e.target.value)}
                    />
                    <input 
                    placeholder="whatsapp"
                    value={whatsapp}
                    onChange={e => setWhatsapp(e.target.value)} 
                    />
                    <div className="input-group">{/* Crio uma div pra ficar um do lado do outro */}
                        <input 
                        placeHolder="Cidade"
                        value={city}
                        onChange={e => setCity(e.target.value)}
                        />
                        <input 
                        placeholder="UF" 
                        style={{width: 80}}
                        value={uf}
                        onChange={e => setUf(e.target.value)}
                        />
                        
                    </div>
                    <button className="button" type="submit">Cadastrar</button> 
                </form>
            </div>
        </div>
    );
}
