import React, {useState} from 'react';//useState para usar o conceito de estado
import Header from './Header';

function App() {
  //o useState retorna um array da seguinte forma [numero, e uma função de atualização do numero]
  const [counter, setCounter]= useState(0);
  

  function increment(){
      setCounter(counter+1);
  }
  
  return (
    <div>
      <Header>Contador: {counter} </Header>  
      <button onClick={increment}> Incrementar </button>{/*toda vez que ouver o click a funcao increment é chamada*/}
    </div>
    );
}

export default App;
